// Regular function 
function add(a, b) {
  return a + b;
}

// Arrow function equivalent
const add = (a, b) => a + b;

console.log(add(5, 3)); // Output: 8

function greet(name, callback) {
  console.log("Hello, " + name);
  callback();
}

function afterGreeting() {
  console.log("How are you?");
}

greet("Alice", afterGreeting);

console.log("Start");
setTimeout(() => console.log("Async Task"), 1000);
console.log("End");

// Output:
// Start
// End
// Async Task (after 1 sec)

var x = 10;
let y = 20;
const z = 30;

const person = {
  name: "John",
  age: 30,
  greet() {
    console.log(`Hello, I am ${this.name}`);
  }
};
console.log(person.name); // John
person.greet(); // Hello, I am John

const obj = {
  name: "Alice",
  greet: function() {
    console.log(this.name);
  }
};
obj.greet(); // Alice

const person = {
  name: "Alice",
  greet: function() {
    setTimeout(() => {
      console.log(this.name);
    }, 1000);
  }
};
person.greet(); // Alice (Arrow function keeps 'this' context)

const obj = {
  name: "Bob",
  greet: () => {
    console.log(this.name); // Undefined (Arrow function does not bind `this`)
  }
};
obj.greet();

const numbers = [1, 2, 3];
